// Process form data from webhook
const body = $json.body || $json;
const projectDescription = body.projectDescription || body['Project Description'] || '';
const boardId = body.boardId || body['Monday Board ID'] || '';
const groupId = body.groupId || body['Group ID (Optional)'] || null;
const groupName = body.groupName || body['Group Name (Optional)'] || null;
const assigneeEmails = body.assigneeEmails || null;
const totalHours = body.totalHours || body['Total Hours'] || null;

// Validation
if (!projectDescription || projectDescription.length < 10) {
  return [{
    json: {
      success: false,
      error: 'Project description must be at least 10 characters long',
      timestamp: new Date().toISOString()
    }
  }];
}

if (!boardId || !/^\d+$/.test(boardId)) {
  return [{
    json: {
      success: false,
      error: 'Board ID must be numbers only',
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    projectDescription: projectDescription.trim(),
    boardId: boardId.trim(),
    groupId: groupId ? groupId.trim() : null,
    groupName: groupName ? groupName.trim() : null,
    assigneeEmails: assigneeEmails ? assigneeEmails.trim().split(',').map(email => email.trim()).filter(email => email) : [],
    totalHours: totalHours ? parseFloat(totalHours) : null,
    timestamp: new Date().toISOString()
  }
}];

// Parse AI response - AI only, no fallback
const validatedData = $('Validate Input Data').item.json;
let tasks = [];
let aiGroupName = null;

// Extract AI response
const aiResponse = $json.choices[0].message.content;
console.log('AI Response:', aiResponse);

// Find JSON in the AI response
const jsonMatch = aiResponse.match(/{[\s\S]*}/s);
if (!jsonMatch) {
  throw new Error('No JSON found in AI response');
}

const aiData = JSON.parse(jsonMatch[0]);
console.log('Parsed AI data:', JSON.stringify(aiData));

if (!aiData.tasks || !Array.isArray(aiData.tasks) || aiData.tasks.length === 0) {
  throw new Error('AI did not return valid tasks array');
}

tasks = aiData.tasks.slice(0, 10);
aiGroupName = aiData.groupName || 'AI Tasks';

// Ensure all tasks have trackedHours
tasks = tasks.map(task => {
  if (!task.trackedHours) {
    if (task.estimatedHours) {
      // Generate realistic tracked hours between 30-90% of estimated
      const percentComplete = 0.3 + Math.random() * 0.6; // 30% to 90%
      task.trackedHours = Math.round(task.estimatedHours * percentComplete * 10) / 10; // Round to 1 decimal
    } else {
      // If no estimated hours, generate some realistic tracked hours (2-15 hours)
      task.trackedHours = Math.round((2 + Math.random() * 13) * 10) / 10;
    }
  }
  return task;
});

// If we have assignee emails, assign them to tasks
if (validatedData.assigneeEmails && validatedData.assigneeEmails.length > 0) {
  tasks = tasks.map((task, index) => {
    const assigneeIndex = index % validatedData.assigneeEmails.length;
    return {
      ...task,
      assignee: validatedData.assigneeEmails[assigneeIndex]
    };
  });
}

return [{
  json: {
    tasks: tasks,
    projectDescription: validatedData.projectDescription,
    boardId: validatedData.boardId,
    groupId: validatedData.groupId,
    groupName: validatedData.groupName,
    aiGroupName: aiGroupName,
    assigneeEmails: validatedData.assigneeEmails,
    totalHours: validatedData.totalHours,
    aiUsed: true,
    tasksCount: tasks.length
  }
}];

// Lookup all unique assignee emails BEFORE creating items
const data = $json;
const tasks = data.tasks || [];

console.log('=== PRE-CREATION USER LOOKUP ===');
console.log('Total tasks:', tasks.length);

// Extract unique emails from tasks
const uniqueEmails = new Set();
tasks.forEach(task => {
  if (task.assignee && typeof task.assignee === 'string' && task.assignee.includes('@')) {
    uniqueEmails.add(task.assignee.trim());
  }
});

console.log('Unique emails to lookup:', Array.from(uniqueEmails));

// Always pass through data, with or without emails
if (uniqueEmails.size === 0) {
  console.log('No emails to lookup, passing through with empty map');
  return [{
    json: {
      ...data,
      userLookupMap: {},
      emailsToLookup: [],
      skipLookup: true
    }
  }];
}

// Build GraphQL query for all emails at once
const emailsArray = Array.from(uniqueEmails);
const emailsString = emailsArray.map(email => `\"${email}\"`).join(', ');
const query = `
  query {
    users(emails: [${emailsString}]) {
      id
      name
      email
    }
  }
`;

console.log('GraphQL query for bulk lookup:', query);

return [{
  json: {
    ...data,
    graphqlQuery: query,
    emailsToLookup: emailsArray,
    skipLookup: false
  }
}];

// Process bulk user lookup results and create email->userId map
const lookupResult = $json;
const originalData = $('Prepare Bulk User Lookup').item.json;

console.log('=== BULK USER LOOKUP RESULTS ===');
console.log('Skip lookup flag:', originalData.skipLookup);
console.log('Lookup result:', JSON.stringify(lookupResult, null, 2));
console.log('Original data preserved:', JSON.stringify(originalData, null, 2));

let userLookupMap = {};

if (originalData.skipLookup) {
  console.log('Lookup was skipped, using empty map');
} else if (lookupResult.data && lookupResult.data.users && Array.isArray(lookupResult.data.users)) {
  console.log(`Found ${lookupResult.data.users.length} users in lookup response`);
  lookupResult.data.users.forEach(user => {
    if (user.email && user.id) {
      userLookupMap[user.email.toLowerCase()] = parseInt(user.id);
      console.log(`✅ Mapped ${user.email} -> ${user.id}`);
    } else {
      console.log(`⚠️ User missing email or id:`, user);
    }
  });
  console.log('Created user lookup map:', JSON.stringify(userLookupMap));
} else if (lookupResult.errors) {
  console.log('❌ GraphQL errors:', JSON.stringify(lookupResult.errors));
} else {
  console.log('❌ No users found in response, full response:', JSON.stringify(lookupResult));
}

// Make sure we pass through ALL original data fields including totalHours
return [{
  json: {
    tasks: originalData.tasks,
    projectDescription: originalData.projectDescription,
    boardId: originalData.boardId,
    groupId: originalData.groupId,
    groupName: originalData.groupName,
    aiGroupName: originalData.aiGroupName,
    assigneeEmails: originalData.assigneeEmails,
    totalHours: originalData.totalHours,
    aiUsed: originalData.aiUsed,
    tasksCount: originalData.tasksCount,
    userLookupMap: userLookupMap
  }
}];

// Split tasks and prepare for creation WITH owner IDs
// Get the original data with tasks from Process Bulk Lookup node
const parseTasksData = $('Process Bulk Lookup').item.json;
console.log('Data from Process Bulk Lookup:', JSON.stringify(parseTasksData, null, 2));

// Get the current input (might be from IF node or Create Group node)
const currentData = $json;
const userLookupMap = parseTasksData.userLookupMap || {};

// Determine final group ID
let finalGroupId;
if (parseTasksData.groupId) {
  // User provided a group ID
  finalGroupId = parseTasksData.groupId;
} else if (parseTasksData.groupName && parseTasksData.groupName.toLowerCase() === 'to-do') {
  // Special case for To-Do group
  finalGroupId = 'new_group29179';
} else if (currentData.id) {
  // We just created a new group, use its ID
  finalGroupId = currentData.id;
  console.log('Using newly created group ID:', finalGroupId);
} else {
  // Fallback to To-Do group
  finalGroupId = 'new_group29179';
}

console.log('Using final group ID:', finalGroupId);

const tasks = parseTasksData.tasks || [];
console.log('Tasks found:', tasks.length);
console.log('User lookup map:', userLookupMap);

if (!Array.isArray(tasks) || tasks.length === 0) {
  return [{
    json: {
      error: 'No tasks array found',
      timestamp: new Date().toISOString()
    }
  }];
}

// Split tasks with owner IDs already resolved
const outputs = [];
tasks.forEach((task, index) => {
  let ownerId = null;

  // Lookup owner ID if assignee is an email
  if (task.assignee && typeof task.assignee === 'string' && task.assignee.includes('@')) {
    const emailLower = task.assignee.toLowerCase();
    ownerId = userLookupMap[emailLower] || null;
    console.log(`Task ${index + 1}: ${task.title} - Assignee: ${task.assignee} - Owner ID: ${ownerId}`);
  }

  outputs.push({
    json: {
      boardId: parseTasksData.boardId,
      groupId: finalGroupId,
      task: task,
      taskIndex: index + 1,
      totalTasks: tasks.length,
      projectDescription: parseTasksData.projectDescription,
      ownerId: ownerId // Include resolved owner ID
    }
  });
});

console.log('Split into', outputs.length, 'task items');
return outputs;

// Prepare item creation with ALL fields including owner
const data = $json;
const task = data.task || {};
const ownerId = data.ownerId;

console.log('=== PREPARING ITEM WITH OWNER ===');
console.log('Task:', task.title);
console.log('Owner ID:', ownerId);
console.log('Estimated Hours:', task.estimatedHours);
console.log('Tracked Hours (AI generated):', task.trackedHours);

// Build column values for additional fields
const columnValues = {};

// Add description to Notes column
if (task.description) {
  columnValues.text9 = task.description.substring(0, 255);
}

// Add priority
if (task.priority) {
  const priorityMap = {
    'high': { index: 110, label: 'High' },
    'medium': { index: 109, label: 'Medium' },
    'low': { index: 7, label: 'Low' }
  };
  const priorityKey = task.priority.toLowerCase();
  columnValues.priority_1 = priorityMap[priorityKey] || priorityMap['medium'];
}

// Add owner ID if we have it
if (ownerId) {
  const ownerValue = {
    personsAndTeams: [
      {
        id: parseInt(ownerId),
        kind: "person"
      }
    ]
  };
  columnValues.project_owner = ownerValue;
  console.log('✅ Setting owner with ID:', ownerId);
}

// Add estimated hours (always add if provided)
if (task.estimatedHours !== undefined && task.estimatedHours !== null) {
  columnValues.numbers = task.estimatedHours.toString();
  console.log('✅ Setting estimated hours:', task.estimatedHours);
}

// NOTE: Time tracking (duration_mktkxg1g) cannot be set via API
// Monday.com API limitation: "This column type is not supported yet in the API"
// The AI generates trackedHours but we cannot apply them to the time_tracking column
if (task.trackedHours && task.trackedHours > 0) {
  console.log('⚠️ Time tracking hours generated by AI:', task.trackedHours, 'hours');
  console.log('   (Cannot be set via API due to Monday.com limitation)');
}

console.log('Column values to set:', JSON.stringify(columnValues, null, 2));

return {
  json: {
    ...data,
    columnValues: columnValues,
    columnValuesString: JSON.stringify(columnValues)
  }
};

const results = $input.all();
const summary = {
  success: true,
  message: 'Successfully created ' + results.length + ' tasks in Monday.com!',
  tasksCreated: results.length,
  boardId: results[0] ? results[0].json.boardId : null,
  timestamp: new Date().toISOString()
};

return [{ json: summary }];
