// Process form data from webhook
const body = $json.body || $json;
const projectDescription = body.projectDescription || body['Project Description'] || '';
const boardId = body.boardId || body['Monday Board ID'] || '';
const groupId = body.groupId || body['Group ID (Optional)'] || null;
const groupName = body.groupName || body['Group Name (Optional)'] || null;
const assigneeEmails = body.assigneeEmails || null;

// Validation
if (!projectDescription || projectDescription.length < 10) {
  return [{
    json: {
      success: false,
      error: 'Project description must be at least 10 characters long',
      timestamp: new Date().toISOString()
    }
  }];
}

if (!boardId || !/^\d+$/.test(boardId)) {
  return [{
    json: {
      success: false,
      error: 'Board ID must be numbers only',
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    projectDescription: projectDescription.trim(),
    boardId: boardId.trim(),
    groupId: groupId ? groupId.trim() : null,
    groupName: groupName ? groupName.trim() : null,
    assigneeEmails: assigneeEmails ? assigneeEmails.trim().split(',').map(email => email.trim()).filter(email => email) : [],
    timestamp: new Date().toISOString()
  }
}];

// Parse AI response - AI only, no fallback
const validatedData = $('Validate Input Data').item.json;
let tasks = [];
let aiGroupName = null;

// Extract AI response
const aiResponse = $json.choices[0].message.content;
console.log('AI Response:', aiResponse);

// Find JSON in the AI response
const jsonMatch = aiResponse.match(/\{[\s\S]*\}/s);
if (!jsonMatch) {
  throw new Error('No JSON found in AI response');
}

const aiData = JSON.parse(jsonMatch[0]);
console.log('Parsed AI data:', JSON.stringify(aiData));

if (!aiData.tasks || !Array.isArray(aiData.tasks) || aiData.tasks.length === 0) {
  throw new Error('AI did not return valid tasks array');
}

tasks = aiData.tasks.slice(0, 10);
aiGroupName = aiData.groupName || 'AI Tasks';

// If we have assignee emails, assign them to tasks
if (validatedData.assigneeEmails && validatedData.assigneeEmails.length > 0) {
  tasks = tasks.map((task, index) => {
    const assigneeIndex = index % validatedData.assigneeEmails.length;
    return {
      ...task,
      assignee: validatedData.assigneeEmails[assigneeIndex]
    };
  });
}

return [{
  json: {
    tasks: tasks,
    projectDescription: validatedData.projectDescription,
    boardId: validatedData.boardId,
    groupId: validatedData.groupId,
    groupName: validatedData.groupName,
    aiGroupName: aiGroupName,
    assigneeEmails: validatedData.assigneeEmails,
    aiUsed: true,
    tasksCount: tasks.length
  }
}];

// Get the original task data from Parse AI Tasks node
const parseTasksData = $('Parse AI Tasks').item.json;
console.log('Original task data:', JSON.stringify(parseTasksData));

// Get current data (might be group creation result)
const currentData = $json;
console.log('Current data:', JSON.stringify(currentData));

// Determine final group ID - prioritize "To-Do" group
let finalGroupId;
if (parseTasksData.groupId) {
  // Use existing group ID from input
  finalGroupId = parseTasksData.groupId;
} else if (parseTasksData.groupName && parseTasksData.groupName.toLowerCase() === 'to-do') {
  // Use the known "To-Do" group ID
  finalGroupId = 'new_group29179';
} else {
  // Use newly created group ID or fall back to "To-Do" group
  finalGroupId = currentData.id || 'new_group29179';
}

console.log('Using group ID:', finalGroupId);

// Get tasks from the original parse data
const tasks = parseTasksData.tasks || [];
console.log('Tasks found:', tasks.length);

if (!Array.isArray(tasks) || tasks.length === 0) {
  return [{
    json: {
      error: 'No tasks array found in original data',
      parseTasksData: parseTasksData,
      currentData: currentData,
      timestamp: new Date().toISOString()
    }
  }];
}

// Split tasks into individual items
const outputs = [];
tasks.forEach((task, index) => {
  outputs.push({
    json: {
      boardId: parseTasksData.boardId,
      groupId: finalGroupId,
      task: task,
      taskIndex: index + 1,
      totalTasks: tasks.length,
      projectDescription: parseTasksData.projectDescription
    }
  });
});

console.log('Split into', outputs.length, 'task items for group:', finalGroupId);
return outputs;

// Create Monday item and preserve task data
const taskData = $json;
console.log('Creating item for task:', JSON.stringify(taskData.task));
console.log('Task index:', taskData.taskIndex, 'of', taskData.totalTasks);

// Pass through all the data we need
return {
  json: {
    ...taskData,
    // Add any preprocessing here
    readyForCreation: true
  }
};

// Combine Monday.com result with original task data
// The Monday.com Create Item node only returns the item ID, but we need the original task data
// We need to get the task data from a node that preserved it in the workflow chain

const mondayResult = $json; // This has the item ID from Monday - Create Item

console.log('Monday result:', JSON.stringify(mondayResult));
console.log('Current item index:', $itemIndex);

// Try to get the original task data from the Prep Create Item node for this specific item
let originalTaskData;
try {
  // Get task data from the Prep Create Item node for the current item index
  const prepData = $('Prep Create Item').item.json;
  console.log('Found prep data:', JSON.stringify(prepData));
  originalTaskData = prepData;
} catch (error) {
  console.log('Error accessing prep data:', error.message);
  // In runOnceForEachItem mode, we can't use $input.first()
  // Instead, get the current input data directly
  console.log('Trying to access from $input.item');
  const inputData = $input.item.json;
  console.log('Input data:', JSON.stringify(inputData));
  originalTaskData = inputData;
}

if (!originalTaskData) {
  console.error('ERROR: No task data found!');
  return {
    json: {
      error: 'Task data missing in Combine Item Data',
      mondayResult: mondayResult,
      itemIndex: $itemIndex,
      timestamp: new Date().toISOString()
    }
  };
}

// The task data comes from the Prep Create Item node, not the Monday Create Item
// So we need to access the paired data correctly
const taskFromPrep = originalTaskData.task || originalTaskData;

return {
  json: {
    // Monday.com response
    itemId: mondayResult.id,
    mondayItem: mondayResult,
    // Original task data - ensure all fields are passed through
    task: originalTaskData.task,
    boardId: originalTaskData.boardId,
    groupId: originalTaskData.groupId,
    taskIndex: originalTaskData.taskIndex,
    totalTasks: originalTaskData.totalTasks,
    projectDescription: originalTaskData.projectDescription
  }
};

// Lookup user ID from email if assignee is an email
const data = $json;
const task = data.task || {};

console.log('=== USER LOOKUP PREP ===');
console.log('Full data received:', JSON.stringify(data, null, 2));
console.log('Task data:', JSON.stringify(task, null, 2));
console.log('Task title:', task.title);
console.log('Assignee value:', task.assignee);
console.log('Assignee type:', typeof task.assignee);

// Check if assignee is an email - more robust check
if (task.assignee && typeof task.assignee === 'string') {
  const assigneeStr = task.assignee.trim();
  console.log('Trimmed assignee:', assigneeStr);

  // Enhanced email detection - check for @ symbol and basic email pattern
  const emailPattern = /^[\w.-]+@[\w.-]+\.[\w]+$/;
  const containsAt = assigneeStr.includes('@');
  const isValidEmail = emailPattern.test(assigneeStr);

  console.log('Contains @:', containsAt);
  console.log('Matches email pattern:', isValidEmail);

  if (containsAt && isValidEmail) {
    console.log('‚úÖ Email detected, setting up lookup for:', assigneeStr);

    return {
      json: {
        ...data,
        lookupEmail: assigneeStr,
        needsLookup: true
      }
    };
  } else {
    console.log('‚ùå Not a valid email format');
  }
} else {
  console.log('‚ùå No assignee or not a string');
}

// No lookup needed, pass through
console.log('üîÑ No lookup needed, passing through');
return {
  json: {
    ...data,
    needsLookup: false,
    userId: null
  }
};

// Use Monday.com API to lookup user by email
const email = $json.lookupEmail;
console.log('Looking up user with email:', email);

// We'll use a simple HTTP request with the Monday.com API token
// The credentials will be available through the workflow
const query = `
  query {
    users(emails: ["${email}"]) {
      id
      name
      email
    }
  }
`;

// For now, we'll pass the email through and handle the lookup in the next node
// This is because we need to use the HTTP Request node with proper auth
return {
  json: {
    ...$json,
    graphqlQuery: query,
    lookupEmail: email
  }
};

// Process user lookup result
const lookupResult = $json;
// In runOnceForEachItem mode, we can't use $input.first()
// Instead, get the current input data directly
const originalData = $input.item.json;

console.log('=== USER LOOKUP RESULT ===');
console.log('Full lookup result:', JSON.stringify(lookupResult, null, 2));
console.log('Original email:', originalData.lookupEmail);

let userId = null;
if (lookupResult.data && lookupResult.data.users && lookupResult.data.users.length > 0) {
  userId = parseInt(lookupResult.data.users[0].id);
  console.log('‚úÖ Found user ID:', userId);
  console.log('User name:', lookupResult.data.users[0].name);
  console.log('User email:', lookupResult.data.users[0].email);
} else if (lookupResult.errors) {
  console.log('‚ùå GraphQL errors:', JSON.stringify(lookupResult.errors));
} else {
  console.log('‚ùå User not found for email:', originalData.lookupEmail);
  console.log('Response structure:', Object.keys(lookupResult));
}

return {
  json: {
    ...originalData,
    userId: userId,
    userFound: userId !== null,
    lookupResponse: lookupResult
  }
};

// Now we have the combined data with user lookup results
const data = $json;
const task = data.task || {};
const itemId = data.itemId;
const boardId = data.boardId;
const userId = data.userId;
const userFound = data.userFound;

console.log('=== PREPARE COLUMN VALUES ===');
console.log('Combined data received:', JSON.stringify(data));
console.log('Task:', JSON.stringify(task));
console.log('Item ID:', itemId);
console.log('User ID found:', userId, 'Type:', typeof userId);
console.log('User found flag:', userFound);

// Create column values with proper Monday.com format
const columnValues = {};

// Add description to Notes column
if (task.description) {
  columnValues.text9 = task.description.substring(0, 255);
  console.log('Added description to Notes');
}

// Add priority as status using proper format  
if (task.priority) {
  const priorityMap = {
    'high': { index: 110, label: 'High' },
    'medium': { index: 109, label: 'Medium' },
    'low': { index: 7, label: 'Low' }
  };

  const priorityKey = task.priority.toLowerCase();
  const priorityValue = priorityMap[priorityKey] || priorityMap['medium'];

  columnValues.priority_1 = priorityValue;
  console.log('Added priority:', priorityValue);
}

// Set owner based on user lookup result
// For n8n's Monday.com node, just pass the user ID as string
if (userId && userFound) {
  columnValues.project_owner = String(userId);
  console.log('‚úÖ Setting owner with user ID:', columnValues.project_owner);
} else if (task.assignee && typeof task.assignee === 'string') {
  // Store as text in Notes if user not found
  columnValues.text9 = (columnValues.text9 || '') + ' (Assignee: ' + task.assignee + ')';
  console.log('‚ö†Ô∏è User not found, added assignee to notes:', task.assignee);
} else {
  console.log('‚ùå No user ID or assignee available');
}

// Add estimated hours
if (task.estimatedHours) {
  columnValues.numbers = task.estimatedHours.toString();
  console.log('Added estimated hours:', task.estimatedHours);
}

console.log('=== FINAL COLUMN VALUES ===');
console.log(JSON.stringify(columnValues, null, 2));

return {
  json: {
    boardId: boardId,
    itemId: itemId,
    columnValues: JSON.stringify(columnValues),
    task: task,
    debugInfo: {
      userId: userId,
      userFound: userFound,
      hasOwner: !!columnValues.project_owner
    }
  }
};

const results = $input.all();
const summary = {
  success: true,
  message: 'Successfully created ' + results.length + ' tasks in Monday.com!',
  tasksCreated: results.length,
  boardId: results[0] ? results[0].json.boardId : null,
  timestamp: new Date().toISOString()
};

return [{ json: summary }];
